rss.channel:
    {{ if eq .Title .Site.Title }}{{ .Site.Title }}{{ else }}{{ .Site.Title }} – {{ .Title }}{{ end }}
    {{ .Permalink }}
    Recent content {{ if ne  .Title  .Site.Title }}{{ with .Title }}in {{.}} {{ end }}{{ end }}on {{ .Site.Title }}
    Hugo -- gohugo.io{{ with .Site.LanguageCode }}
    {{.}}{{end}}{{ with .Site.Author.email }}
    {{.}}{{ with $.Site.Author.name }} ({{.}}){{end}}{{end}}{{ with .Site.Author.email }}
    {{.}}{{ with $.Site.Author.name }} ({{.}}){{end}}{{end}}{{ with .Site.Copyright }}
    {{.}}{{end}}{{ if not .Date.IsZero }}
    {{ .Date.Format "Mon, 02 Jan 2006 15:04:05 -0700" | safeHTML }}{{ end }}
    {{ with .OutputFormats.Get "RSS" }}
	  {{ printf "" .Permalink .MediaType | safeHTML }}
    {{ end }}

    {{/* 페이지 선택 및 중복 제거 */}}
    {{ $rssPages := slice }}
    {{ if .IsHome }}
      {{ $rssPages = first 50 .Site.RegularPages.ByDate.Reverse }}
    {{ else }}
      {{ $rssPages = first 50 .Pages.ByDate.Reverse }}
    {{ end }}

    {{/* 중복 URL 제거를 위한 맵 */}}
    {{ $seenURLs := dict }}
    {{ $uniquePages := slice }}
    {{ range $rssPages }}
      {{ if not (index $seenURLs .Permalink) }}
        {{ $seenURLs = merge $seenURLs (dict .Permalink true) }}
        {{ $uniquePages = $uniquePages | append . }}
      {{ end }}
    {{ end }}

    {{ range $uniquePages }}
    {{ $cleanContent := .Content }}
    {{/* Meta 태그 제거 */}}
    {{ $cleanContent = $cleanContent | replaceRE `]*>` "" }}
    {{ $cleanContent = $cleanContent | replaceRE `
]*style="display:none"[^>]*>.*?` "" }}
    {{/* 상대 URL을 절대 URL로 변환 */}}
    {{ $cleanContent = $cleanContent | replaceRE `href="#([^"]*)"` (printf `href="%s#\$1"` .Permalink) }}
    {{ $cleanContent = $cleanContent | replaceRE `src="/([^"]*)"` (printf `src="%s/\$1"` $.Site.BaseURL) }}
    {{/* 불필요한 클래스 제거 */}}
    {{ $cleanContent = $cleanContent | replaceRE ` class="[^"]*"` "" }}
    {{ $cleanContent = $cleanContent | replaceRE ` id="[^"]*"` "" }}

    {{/* Summary 생성 (첫 200자) */}}
    {{ $summary := .Summary }}
    {{ if not $summary }}
      {{ $summary = .Content | plainify | truncate 200 }}
    {{ end }}
    {{ $summary = $summary | replaceRE `<[^>]*>` "" }}



      {{ .Title | html }}
      {{ .Permalink }}
      {{ .Date.Format "Mon, 02 Jan 2006 15:04:05 -0700" | safeHTML }}
      {{ with .Site.Author.email }}{{.}}{{ with $.Site.Author.name }} ({{.}}){{end}}{{end}}
      {{ .Permalink }}
      {{ $summary | html }}
      {{ $cleanContent | safeHTML }}

    {{ end }}
