rss.channel:
    {{ if eq .Title .Site.Title }}{{ .Site.Title }}{{ else }}{{ .Site.Title }} – {{ .Title }}{{ end }}
    {{ .Permalink }}
    Recent content {{ if ne  .Title  .Site.Title }}{{ with .Title }}in {{.}} {{ end }}{{ end }}on {{ .Site.Title }}
    Hugo -- gohugo.io{{ with .Site.LanguageCode }}
    {{.}}{{end}}{{ with .Site.Author.email }}
    {{.}}{{ with $.Site.Author.name }} ({{.}}){{end}}{{end}}{{ with .Site.Author.email }}
    {{.}}{{ with $.Site.Author.name }} ({{.}}){{end}}{{end}}{{ with .Site.Copyright }}
    {{.}}{{end}}{{ if not .Date.IsZero }}
    {{ .Date.Format "Mon, 02 Jan 2006 15:04:05 -0700" | safeHTML }}{{ end }}
    {{ with .OutputFormats.Get "RSS" }}
	  {{ printf "" .Permalink .MediaType | safeHTML }}
    {{ end }}

    {{/* --- 수정된 부분: RSS에 포함할 페이지 필터링 로직 --- */}}
    {{ $filteredPages := $.Site.RegularPages }}
    {{ if $.IsHome }}
      {{/* 홈페이지 RSS일 경우, 모든 정규 페이지 중 최신 50개 */}}
      {{ $filteredPages = $.Site.RegularPages }}
    {{ else if $.IsSection }}
      {{/* 섹션 페이지 RSS일 경우, 해당 섹션 내 정규 페이지 중 최신 50개 */}}
      {{ $filteredPages = $.Page.RegularPages }}
    {{ else if $.IsTerm }}
      {{/* Taxonomy (태그/카테고리) 페이지 RSS일 경우, 해당 태그/카테고리 내 정규 페이지 중 최신 50개 */}}
      {{ $filteredPages = $.Page.RegularPages }}
    {{ end }}

    {{/* type: "docs"인 페이지들만 포함 */}}
    {{ $rssPages := where $filteredPages "Type" "docs" }}
    {{/* 최신순으로 정렬 후 상위 50개만 가져오기 */}}
    {{ $rssPages = first 50 $rssPages.ByDate.Reverse }}
    {{/* --- 수정 끝 --- */}}

    {{ range $rssPages }}


      {{ .Title }}
      {{ .Permalink }}
      {{ .Date.Format "Mon, 02 Jan 2006 15:04:05 -0700" | safeHTML }}
      {{ with .Site.Author.email }}{{.}}{{ with $.Site.Author.name }} ({{.}}){{end}}{{end}}
      {{ .Permalink }}

        {{/* 피쳐드 이미지 추출 및 HTML에 삽입 */}}
        {{ $img := (.Resources.ByType "image").GetMatch "*featured*" }}
        {{ with $img }}
        {{ $img := .Resize "640x" }}
        {{ printf "<img src=\"%s\" width=\"%d\" height=\"%d\"/>" $img.Permalink $img.Width $img.Height | safeHTML }}
        {{ end }}
        {{/* 본문 내용 전체를 HTML로 변환하여 삽입 */}}
        {{ .Content | html }}


    {{ end }}
